/*
Implement wildcard pattern matching with support for '?' and '*'.

'?' Matches any single character.
'*' Matches any sequence of characters (including the empty sequence).

The matching should cover the entire input string (not partial).

The function prototype should be:
bool isMatch(const char *s, const char *p)

Some examples:
isMatch("aa","a") → false
isMatch("aa","aa") → true
isMatch("aaa","aa") → false
isMatch("aa", "*") → true
isMatch("aa", "a*") → true
isMatch("ab", "?*") → true
isMatch("aab", "c*a*b") → false
 */
#include <iostream>

using namespace std;

class Solution {
public:
    bool isMatch(const char *s, const char *p) {
        int i = 0, j = 0;
        if( NULL == s || NULL == p )
        	return false;
        int star = 0;
        return match(s, i, p, j, star);
    }
private:
	bool match(const char * s, int i, const char * p, int j, int & star);
};

bool Solution::match(const char * s, int i, const char * p, int j, int & star){
	while( s[i] != '\0' && p[j] != '\0' ){
		if( p[j] == '?' ){
			i++;
			j++;
			continue;
		}
		if( p[j] == '*' ){
			star++;
			int temp = star;

			if( p[j + 1] == '\0' )
				return true;
			if( p[j + 1] == '*' ){
				j++;
				continue;
			}
			//s += i;i = 0;
			//p += j + 1;j = 0;
			while( s[i] != '\0' ){
				bool rst = match(s, i, p, j+1, star);
				if(rst)
					return true;
				if( temp != star )
					return false;
				i++;
			}
			return false;
		}
		if( s[i] == p[j] ){
			i++;
			j++;
			continue;
		}

		return false;
	}
	while( p[j] == '*' )
		j++;
	if( s[i] == '\0' && p[j] == '\0' )
		return true;//match
	if( s[i] == '\0' )//pattern string is longer than souce string
			star++;
	return false;//partial match
}

int main(){
	Solution sol;
	cout << sol.isMatch("", 
		"**");
	return 0;
}